<?php

namespace CM\CMBundle\Entity;

use Doctrine\ORM\EntityRepository as BaseRepository;
use Symfony\Bundle\FrameworkBundle\Controller\Controller;

/**
 * EventRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class EventRepository extends BaseRepository
{
    static protected function getOptions(array $options = array())
    {
        $options = array_merge(array(
            'locale'        => 'en'
        ), $options);
        
        return array_merge(array(
            'exclude'       => null,
            'userId'       => null,
            'pageId'       => null,
            'archive'       => null, 
            'categoryId'   => null,
            'currentUserId' => null,
            'mainImageOnly' => false,
            'paginate'      => true,
            'locales'       => array_values(array_merge(array('en' => 'en'), array($options['locale'] => $options['locale']))),
            'protagonist'  => null,
            'protagonists'  => false,
            'status' => null,
            'tags'  => false,
            'limit'         => 25,
        ), $options);
    }
    
    public function getEvents(array $options = array())
    {
        $options = self::getOptions($options);

        $parameters = array(
            'now' => new \DateTime,
        );
        
        $count = $this->getEntityManager()->createQueryBuilder()
            ->select('count(d.id)')
            ->from('CMBundle:EventDate','d')
            ->join('d.event', 'e')
            ->join('e.post', 'p');
                    
        $query = $this->getEntityManager()->createQueryBuilder()
            ->select('d, e, t, ec, ect, i, p, l, c, u, lu, cu, pg')
            ->from('CMBundle:EventDate','d')
            ->join('d.event', 'e')
            ->join('e.translations', 't', 'with', 't.locale IN (:locales)')
            ->join('e.category', 'ec')
            ->join('ec.translations', 'ect', 'with', 'ect.locale = :locale')
            ->leftJoin('e.image', 'i')
            ->join('e.post', 'p')
            ->join('p.user', 'u')
            ->leftJoin('p.page', 'pg')
            ->leftJoin('p.likes', 'l')
            ->leftJoin('p.comments', 'c')
            ->leftJoin('l.user', 'lu')
            ->leftJoin('c.user', 'cu');

        if ($options['protagonists']) {
            $query->addSelect('eu, us')
                ->join('e.entityUsers', 'eu', '', '', 'eu.userId')
                ->join('eu.user', 'us');
        }
        
        if ($options['userId']) {
            $count->join('e.entityUsers', 'eu', '', '', 'eu.userId')
                ->andWhere('eu.userId = :user_id')
                ->andWhere('eu.status = '.EntityUser::STATUS_ACTIVE);
            $query->join('e.entityUsers', 'eu', '', '', 'eu.userId')
                ->andWhere('eu.userId = :user_id')
                ->andWhere('eu.status = '.EntityUser::STATUS_ACTIVE);
            $parameters['user_id'] = $options['userId'];
        }
        
        if ($options['pageId']) {
            $count->andWhere('p.pageId = :page_id');
            $query->andWhere('p.pageId = :page_id');
            $parameters['page_id'] = $options['pageId'];
        }
        
        if ($options['categoryId']) {
            $count->andWhere('e.category = :category_id');
            $query->andWhere('e.category = :category_id');
            $parameters['category_id'] = $options['categoryId'];
        }
        
        if ($options['archive']) {
            $count->andWhere('d.start <= :now');
            $query->andWhere('d.start <= :now')
                ->orderBy('d.start', 'desc');    
        } else {
            $count->andWhere('d.start >= :now');
            $query->andWhere('d.start >= :now')
                ->orderBy('d.start');    
        }
        
        $count->setParameters($parameters);
        $parameters['locale'] = $options['locale'];
        $parameters['locales'] = $options['locales'];
        $query->setParameters($parameters);
        
        return $options['paginate'] ? $query->getQuery()->setHint('knp_paginator.count', $count->getQuery()->getSingleScalarResult()) : $query->setMaxResults($options['limit'])->getQuery()->getResult();
    }
    
    public function getEventsPerMonth($year, $month, array $options = array())
    {
        $options = self::getOptions($options);
        
        return $this->getEntityManager()->createQueryBuilder()
            ->select('d, e, t')
            ->from('CMBundle:EventDate','d')
            ->join('d.event', 'e')
            ->leftJoin('e.translations', 't')
            ->where('SUBSTRING(d.start, 1, 4) = '.$year)
            ->andWhere('SUBSTRING(d.start, 6, 2) = '.$month)
            ->andWhere('t.locale IN (:locales)')->setParameter('locales', $options['locales'])
            ->orderBy('d.start')
            ->getQuery()
            ->getResult();
    }

    public function getEventJoined($id, array $options = array())
    {
        $options = self::getOptions($options);
        
        $query = $this->createQueryBuilder('e')
            ->select('e, t, ec, ect, d, i, p, l, c, u, lu, cu, pg, eu, us')
            ->leftJoin('e.eventDates', 'd')
            ->leftJoin('e.translations', 't')
            ->leftJoin('e.category', 'ec')
            ->leftJoin('ec.translations', 'ect')
            ->leftJoin('e.image', 'i');
        if ($options['mainImageOnly']) {
            $query->andHaving('i.main = '.true);
        }
        return $query->leftJoin('e.post', 'p')
            ->leftJoin('p.likes', 'l')
            ->leftJoin('p.comments', 'c')
            ->leftJoin('p.user', 'u')
            ->leftJoin('l.user', 'lu')
            ->leftJoin('c.user', 'cu')
            ->leftJoin('p.page', 'pg')
            ->leftJoin('e.entityUsers', 'eu')
            ->leftJoin('eu.user', 'us')
            ->andWhere('e.id = :id')->setParameter('id', $id)
            ->andWhere('t.locale IN (:locales)')
            ->andWhere('ect.locale IN (:locales)')
            ->setParameter('locales', $options['locales'])
            ->orderBy('d.start', 'asc')
            ->getQuery()->getSingleResult();
    }
    
    public function getEvent($id, array $options = array())
    {
        $options = self::getOptions($options);
        
        $query = $this->createQueryBuilder('e')
            ->select('e, t, ec, ect, i, p, pl, plu, pc, pcu, u, pg')
            ->join('e.translations', 't', 'with', 't.locale IN (:locales)')->setParameter('locales', $options['locales'])
            ->join('e.category', 'ec')
            ->join('ec.translations', 'ect', 'with', 'ect.locale = :locale')->setParameter('locale', $options['locale'])
            ->leftJoin('e.image', 'i')
            ->join('e.post', 'p')
            ->leftJoin('p.likes', 'pl')
            ->leftJoin('pl.user', 'plu')
            ->leftJoin('p.comments', 'pc')
            ->leftJoin('pc.user', 'pcu')
            ->join('p.user', 'u')
            ->leftJoin('p.page', 'pg')
            ->andWhere('e.id = :id')->setParameter('id', $id);
        if (!is_null($options['slug'])) {
            $query->andWhere('t.slug = :slug')->setParameter('slug', $options['slug']);
        }
        if ($options['protagonists']) {
            $query->addSelect('eu, us');
            if (is_array($options['status'])) {
                $query->join('e.entityUsers', 'eu', 'with', 'eu.status in (:status)', 'eu.userId')
                    ->setParameter('status', $options['status']);
            } elseif (!is_null($options['status'])) {
                $query->join('e.entityUsers', 'eu', 'with', 'eu.status = :status', 'eu.userId')
                    ->setParameter('status', $options['status']);
            } else {
                $query->join('e.entityUsers', 'eu', '', '', 'eu.userId');
            }
            $query->join('eu.user', 'us')
                ->addOrderBy('us.firstName');
        }
        if (!is_null($options['protagonist'])) {
            $query->addSelect('eu')
                ->leftJoin('e.entityUsers', 'eu', 'with', 'eu.userId = :user_id', 'eu.userId')
                ->setParameter('user_id', $options['protagonist']);
        }
        if ($options['tags']) {
            $query->addSelect('eut, ta, tat')
                ->leftJoin('eu.entityUserTags', 'eut', '', '', 'eut.order')
                ->leftJoin('eut.tag', 'ta')
                ->leftJoin('ta.translations', 'tat', 'with', 'tat.locale = :locale');
        }
        return $query->getQuery()->getSingleResult();
    }

    public function getNextDates($options = array())
    {
        $options = self::getOptions($options);
        
        $count = $this->getEntityManager()->createQueryBuilder()
            ->select('count(d.id)')
            ->from('CMBundle:EventDate', 'd')
            ->join('d.event', 'e')
            ->join('e.post', 'p')
            ->andWhere('d.start >= :now')->setParameter('now', new \DateTime);

        $query = $this->getEntityManager()->createQueryBuilder()
            ->select('d, e, t, p, i')
            ->from('CMBundle:EventDate', 'd')
            ->join('d.event', 'e')
            ->leftJoin('e.translations', 't', 'with', 't.locale IN (:locales)')->setParameter('locales', $options['locales'])
            ->join('e.post', 'p')
            ->leftJoin('e.image', 'i')
            ->andWhere('d.start >= :now')->setParameter('now', new \DateTime)
            ->orderBy('d.start');
        if (!is_null($options['exclude'])) {
            $count->andWhere('d.eventId != :exclude')->setParameter('exclude', $options['exclude']);
            $query->andWhere('d.eventId != :exclude')->setParameter('exclude', $options['exclude']);
        }
        if (!is_null($options['userId'])) {
            $count->join('e.entityUsers', 'eu', '', '', 'eu.userId')
                ->andWhere('eu.userId = :user_id')->setParameter('user_id', $options['userId'])
                ->andWhere('eu.status = '.EntityUser::STATUS_ACTIVE);
            $query->join('e.entityUsers', 'eu', '', '', 'eu.userId')
                ->andWhere('eu.userId = :user_id')->setParameter('user_id', $options['userId'])
                ->andWhere('eu.status = '.EntityUser::STATUS_ACTIVE);
        }
        if (!is_null($options['pageId'])) {
            $count->andWhere('p.pageId = :page_id')->setParameter('page_id', $options['pageId']);
            $query->andWhere('p.pageId = :page_id')->setParameter('page_id', $options['pageId']);
        }

        return $options['paginate'] ? $query->getQuery()->setHint('knp_paginator.count', $count->getQuery()->getSingleScalarResult()) : $query->setMaxResults($options['limit'])->getQuery()->getResult();
    }

    public function countNextDates($options = array())
    {
        $options = self::getOptions($options);
        
        $query = $this->getEntityManager()->createQueryBuilder()
            ->select('count(d.id)')
            ->from('CMBundle:EventDate', 'd')
            ->join('d.event', 'e')
            ->join('e.post', 'p')
            ->andWhere('d.start >= :now')->setParameter('now', new \DateTime)
            ->orderBy('d.start');
        if (!is_null($options['userId'])) {
            $query->join('e.entityUsers', 'eu', '', '', 'eu.userId')
                ->andWhere('eu.userId = :user_id')->setParameter('user_id', $options['userId'])
                ->andWhere('eu.status = '.EntityUser::STATUS_ACTIVE);
        }
        if (!is_null($options['pageId'])) {
            $query->andWhere('p.pageId = :page_id')
                ->setParameter('page_id', $options['pageId']);
        }

        return $query->getQuery()
            ->getSingleScalarResult();
    }

    public function getDatesPerEvent($id)
    {   
        return $this->getEntityManager()->createQueryBuilder()
            ->select('d')
            ->from('CMBundle:EventDate','d')
            ->where('d.eventId = :id')->setParameter('id', $id)
            ->orderBy('d.start')
            ->getQuery()
            ->getResult();
    }

    public function getSponsored(array $options = array())
    {   
        $options = self::getOptions($options);
        
        $sponsored = $this->getEntityManager()->createQueryBuilder()
            ->select('partial s.{id, entityId, views, start, end}, partial e.{id}')
            ->from('CMBundle:Sponsored','s')
            ->join('s.entity', 'e', 'with', 'e instance of '.Event::className())
            ->andWhere('s.start <= :now')
            ->andWhere('s.end >= :now')
            ->setParameter(':now', new \DateTime)
            ->getQuery()
            ->getResult();

        if (count($sponsored) == 0) return null;

        shuffle($sponsored);
        $sponsored = array_slice($sponsored, 0, $options['limit']);
        
        $this->getEntityManager()->createQueryBuilder()
            ->update('CMBundle:Sponsored', 's')
            ->set('s.views', 's.views + 1')
            ->where('s.id in (:ids)')->setParameter('ids', array_map(function($i) { return $i->getId(); }, $sponsored))
            ->getQuery()->execute();

        return $this->createQueryBuilder('e')
            ->select('e, d, t, i')
            ->leftJoin('e.eventDates', 'd')
            ->leftJoin('e.translations', 't', 'with', 't.locale IN (:locales)')->setParameter('locales', $options['locales'])
            ->leftJoin('e.image', 'i')
            ->andWhere('e.id IN (:ids)')->setParameter('ids', array_map(function($i) { return $i->getEntityId(); }, $sponsored))
            ->getQuery()
            ->getResult();
    }

    public function getLastByVip($limit, $options = array())
    {
        $options = self::getOptions($options);
        
        return $this->getEntityManager()->createQueryBuilder()
            ->select('d, e, t, i, p, l, c, u, lu, cu, pg')
            ->from('CMBundle:EventDate','d')
            ->join('d.event', 'e')
            ->leftJoin('e.translations', 't')
            ->leftJoin('e.image', 'i')
            ->innerJoin('e.post', 'p')
            ->leftJoin('e.entityUsers', 'eu', '', '', 'eu.userId')
            ->leftJoin('eu.user', 'us')
            ->leftJoin('p.likes', 'l')
            ->leftJoin('p.comments', 'c')
            ->leftJoin('p.user', 'u')
            ->leftJoin('l.user', 'lu')
            ->leftJoin('c.user', 'cu')
            ->leftJoin('p.page', 'pg')
            ->where('t.locale in (:locales)')->setParameter('locales', $options['locales'])
            ->andWhere('u.vip = '.true)
            ->andWhere('d.start >= :now')->setParameter('now', new \DateTime)
            ->orderBy('d.start')
            ->setMaxResults($limit)
            ->getQuery()->getResult();
    }

    public function delete($id)
    {
        $this->createQueryBuilder('e')
            ->delete('CMBundle:Event', 'e')
            ->where('e.id = :id')->setParameter('id', $id)
            ->getQuery()->execute();
    }
}