<?php

namespace CM\CMBundle\Entity;

use Doctrine\ORM\EntityRepository as BaseRepository;

/**
 * PostRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PostRepository extends BaseRepository
{
    static protected function getOptions(array $options = array())
    {
        $options = array_merge(array(
            'locale' => 'en'
        ), $options);
        
        return array_merge(array(
            'entityCreation' => false,
            'entityId' => null,
            'like' => false,
            'object' => null,
            'after' => null,
            'userId' => null,
            'pageId' => null,
            'groupId' => null,
            'inUsers' => array(),
            'inPages' => array(),
            'inGroups' => array(),
            'in' => array(),
            'exclude' => array(),
            'vip' => false,
            'protagonists' => true,
            'locales' => array_values(array_merge(array('en' => 'en'), array($options['locale'] => $options['locale']))),
            'paginate' => true,
            'limit' => null,
            'objects' => array()
        ), $options);
    }

    public function getPostWithSocial($id)
    {
        return $this->createQueryBuilder('p')
            ->select('p, c, cu, l, lu')
            ->leftJoin('p.comments', 'c')
            ->leftJoin('c.user', 'cu')
            ->leftJoin('p.likes', 'l')
            ->leftJoin('l.user', 'lu')
            ->where('p.id = :id')->setParameter('id', $id)
            ->getQuery()->getSingleResult();
    }

    public function getEntity($entityId)
    {
        return $this->createQueryBuilder('p')
            ->select('p, e')
            ->leftJoin('p.entity', 'e')
            ->where('e.id = :id')->setParameter('id', $entityId)
            ->getQuery()->getSingleResult();
    }

    public function getLastPosts($options = array())
    {
        $options = self::getOptions($options);

        $count = $this->createQueryBuilder('p')
            ->select('count(p)');
            
        $query = $this->createQueryBuilder('p')
            ->select('p, u, pl, pc, plu, pcu, e, t, c, ct, i,  ep, epu, epp, epg, epl, epc, eplu, epcu')
            ->join('p.user', 'u')
            ->leftJoin('p.likes', 'pl')
            ->leftJoin('p.comments', 'pc', '', '', 'pc.id')
            ->leftJoin('pl.user', 'plu')
            ->leftJoin('pc.user', 'pcu')
            ->join('p.entity', 'e')
            ->leftJoin('e.translations', 't', 'with', 't.locale in (:locales)')->setParameter('locales', $options['locales'])
            ->leftJoin('e.category', 'c')
            ->leftJoin('c.translations', 'ct', 'with', 'ct.locale = :locale')->setParameter('locale', $options['locale'])
            ->leftJoin('e.image', 'i')
            ->join('e.post', 'ep')
            ->leftJoin('ep.user', 'epu')
            ->leftJoin('ep.page', 'epp')
            ->leftJoin('ep.group', 'epg')
            ->leftJoin('ep.likes', 'epl')
            ->leftJoin('ep.comments', 'epc', '', '', 'epc.id')
            ->leftJoin('epl.user', 'eplu')
            ->leftJoin('epc.user', 'epcu');
        if ($options['vip']) {
            $count->join('p.user', 'u')->andWhere('u.roles like :vip')->setParameter('vip', '%ROLE_VIP%');
            $query->andWhere('p.groupId is null')->andWhere('p.pageId is null')
                ->andWhere('u.roles like :vip')->setParameter('vip', '%s:8:"ROLE_VIP";%');
        } else {
            $query->addSelect('pg, pp')
                ->leftJoin('p.group', 'pg')
                ->leftJoin('p.page', 'pp');
        }
        if ($options['protagonists']) {
            $query->addSelect('eu, euu')
                ->leftJoin('e.entityUsers', 'eu', 'with', 'eu.status = '.EntityUser::STATUS_ACTIVE, 'eu.userId')
                ->leftJoin('eu.user', 'euu');
        }
        if ($options['entityCreation']) {
            $count->andWhere('p.type = '.Post::TYPE_CREATION.' AND p.object in (:objects)')
                ->setParameter('objects', array(Event::className(), Disc::className(), Biography::className(), ImageAlbum::className(), Multimedia::className(), Article::className(), Link::className()));
            $query->andWhere('p.type = '.Post::TYPE_CREATION.' AND p.object in (:objects)')
                ->setParameter('objects', array(Event::className(), Disc::className(), Biography::className(), ImageAlbum::className(), Multimedia::className(), Article::className(), Link::className()));
        }
        if (!is_null($options['entityId'])) {
            $count->leftJoin('p.entity', 'e')
                ->andWhere('e.id = :entity_id')->setParameter('entity_id', $options['entityId']);
            $query->andWhere('e.id = :entity_id')->setParameter('entity_id', $options['entityId']);
            if (!$options['like']) {
                $count->andWhere('p.object != :like')->setParameter('like', Like::className());
                $query->andWhere('p.object != :like')->setParameter('like', Like::className());
            }
        }
        if (!is_null($options['object'])) {
            $count->andWhere('p.object = :object')->setParameter('object', $options['object']);
            $query->andWhere('p.object = :object')->setParameter('object', $options['object']);
        } elseif (count($options['objects'])) {
            $count->andWhere('p.object IN (:objects)')->setParameter('objects', $options['objects']);
            $query->andWhere('p.object IN (:objects)')->setParameter('objects', $options['objects']);
        }
        if (!is_null($options['objectId'])) {
            $count->andWhere('p.objectIds LIKE :object_id')->setParameter('object_id', '%,'.$options['objectId'].',%');
            $query->andWhere('p.objectIds LIKE :object_id')->setParameter('object_id', '%,'.$options['objectId'].',%');
        }
        if (!is_null($options['after'])) {
            $count->andWhere('p.updatedAt > :after')->setParameter('after', $options['after']);
            $query->andWhere('p.updatedAt > :after')->setParameter('after', $options['after']);
        }
        if (!is_null($options['pageId'])) {
            $count->andWhere('p.pageId = :page_id')->setParameter('page_id', $options['pageId']);
            $query->andWhere('p.pageId = :page_id')->setParameter('page_id', $options['pageId']);
        } elseif (!is_null($options['groupId'])) {
            $count->andWhere('p.groupId = :group_id')->setParameter('group_id', $options['groupId']);
            $query->andWhere('p.groupId = :group_id')->setParameter('group_id', $options['groupId']);
        } elseif (!is_null($options['userId'])) {
            $count->andWhere('p.userId = :user_id')->setParameter('user_id', $options['userId']);
            $query->andWhere('p.userId = :user_id')->setParameter('user_id', $options['userId']);
        }
        if (!empty($options['inUsers'])) {
            $count->andWhere('p.userId in (:in_users)')->setParameter('in_users', $options['inUsers']);
            $query->andWhere('p.userId in (:in_users)')->setParameter('in_users', $options['inUsers']);
        } elseif (!empty($options['inPages'])) {
            $count->andWhere('p.pageId in (:in_pages)')->setParameter('in_pages', $options['inPages']);
            $query->andWhere('p.pageId in (:in_pages)')->setParameter('in_pages', $options['inPages']);
        } elseif (!empty($options['inGroups'])) {
            $count->andWhere('p.groupId in (:in_groups)')->setParameter('in_groups', $options['inGroups']);
            $query->andWhere('p.groupId in (:in_groups)')->setParameter('in_groups', $options['inGroups']);
        }
        if (!empty($options['in'])) {
            if (!empty($options['in']['inUsers'])) {
                $count->orWhere('p.userId in (:in_in_users)')->setParameter('in_in_users', $options['in']['inUsers']);
                $query->orWhere('p.userId in (:in_in_users)')->setParameter('in_in_users', $options['in']['inUsers']);
            }
            if (!empty($options['in']['inPages'])) {
                $count->orWhere('p.pageId in (:in_in_pages)')->setParameter('in_in_pages', $options['in']['inPages']);
                $query->orWhere('p.pageId in (:in_in_pages)')->setParameter('in_in_pages', $options['in']['inPages']);
            }
            if (!empty($options['in']['inGroups'])) {
                $count->orWhere('p.groupId in (:in_in_groups)')->setParameter('in_in_groups', $options['in']['inGroups']);
                $query->orWhere('p.groupId in (:in_in_groups)')->setParameter('in_in_groups', $options['in']['inGroups']);
            }
        }
        if (!empty($options['exclude'])) {
            $count->andWhere('p.id not in (:exclude)')->setParameter('exclude', $options['exclude']);
            $query->andWhere('p.id not in (:exclude)')->setParameter('exclude', $options['exclude']);
        }
        $query->orderBy('p.updatedAt', 'desc');
        if (!$options['paginate'] && !is_null($options['limit'])) {
            $query->setMaxResults($options['limit']);
        }

        return $options['paginate'] ? $query->getQuery()->setHint('knp_paginator.count', $count->getQuery()->getSingleScalarResult()) : $query->getQuery()->getResult();
    }

    public function getLastPostFor($userId, $type, $object = null, $objectId = null, $options = array())
    {
        $options = self::getOptions($options);

        $query = $this->createQueryBuilder('p')
            ->select('p')
            ->where('identity(p.user) = :user_id')->setParameter('user_id', $userId)
            ->andWhere('p.type = '.$type);
        if (!is_null($options['after'])) {
            $query->andWhere('p.updatedAt > :time')->setParameter('time', $options['after']);
        }
        if (!is_null($object)) {
            $query->andWhere('p.object = :object')->setParameter('object', $object);
        }
        if (!is_null($objectId)) {
            $query->andWhere('p.objectIds like :object_id')->setParameter('object_id', '%,'.$objectId.',%');
        }
        $query->orderBy('p.updatedAt', 'desc');
        if (!is_null($options['limit'])) {
            $query->setMaxResults($options['limit']);
        }
        return $query->getQuery()->getResult();
    }

