<?php

namespace CM\CMBundle\Entity;

use Doctrine\ORM\EntityRepository as BaseRepository;
use Doctrine\ORM\Query;

/**
 * UserRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class UserRepository extends BaseRepository
{
    static protected function getOptions(array $options = array())
    {
        return array_merge(array(
            'group_id'      => null,
            'page_id'       => null,
            'archive'       => null, 
            'paginate'      => true,
            'limit'         => 25
        ), $options);
    }

    public function getCreatedGroupsIds($user_id)
    {
        return $this->getEntityManager()->createQueryBuilder()
            ->select('g.id')->from('CMBundle:Group', 'g')
            ->leftJoin('g.creator', 'c')
            ->where('c.id = :user_id')->setParameter('user_id', $user_id)
            ->getQuery()->getResult();
    }

    public function getCreatedPagesIds($user_id)
    {
        return $this->getEntityManager()->createQueryBuilder()
            ->select('p.id')->from('CMBundle:Page', 'p')
            ->leftJoin('p.creator', 'c')
            ->where('c.id = :user_id')->setParameter('user_id', $user_id)
            ->getQuery()->getResult();
    }

    public function getFromAutocomplete($fullname, $exclude = array())
    {
        $qb = $this->createQueryBuilder('u');
        return $qb->select('partial u.{id, username, firstName, lastName, img, offset}')
            ->andWhere('u.id NOT IN (:exclude)')->setParameter('exclude', $exclude)
            // ->where('u.IsActive = ?', true)
            ->andWhere('u.enabled = '.true)
            ->andWhere(
                $qb->expr()->orX('CONCAT(u.firstName, CONCAT(\' \', u.lastName)) LIKE :fullname', 'CONCAT(u.lastName, CONCAT(\' \', u.firstName)) LIKE :fullname')
            )->setParameter('fullname', $fullname.'%')
            ->setMaxResults(8)
            ->orderBy('u.vip', 'DESC')
            ->getQuery()->getResult(Query::HYDRATE_ARRAY);
    }

    public function getRequests($userId, $direction = 'incoming', array $options = array())
    {
        $options = self::getOptions($options);
        
        $query = $this->getEntityManager()->createQueryBuilder()
            ->select('r, u, e')
            ->from('CMBundle:Request', 'r')
            ->leftJoin('r.entity', 'e');
        if ($direction == 'incoming') {
            $query->leftJoin('r.user', 'u');
        } elseif ($direction == 'outgoing') {
            $query->leftJoin('r.fromUser', 'u');
        }
        $query->where('u.id = :id')->setParameter('id', $userId)
            ->andWhere('r.status NOT IN ('.Request::STATUS_ACCEPTED.','.Request::STATUS_REFUSED.')')
            ->orderBy('r.createdAt', 'desc');

        return $options['paginate'] ? $query->getQuery() : $query->setMaxResults($options['limit'])->getQuery()->getResult();
    }

    public function getNumberNewRequests($userId)
    {
        return $this->getEntityManager()->createQueryBuilder()
            ->select('count(r.id)')
            ->from('CMBundle:Request', 'r')
            ->leftJoin('r.user', 'u')
            ->where('u.id = :id')->setParameter('id', $userId)
            ->andWhere('r.status = :new')->setParameter('new', Request::STATUS_NEW)
            ->getQuery()->getSingleScalarResult();
    }

    public function updateRequestsStatus($userId, $ids = null, $oldStatus = null, $newStatus = Request::STATUS_PENDING)
    {
        $query = $this->getEntityManager()->createQueryBuilder()
            ->update('CMBundle:Request', 'r')
            ->where('r.user = :id')->setParameter('id', $userId);
        if (!is_null($ids)) {
            $query->andWhere('r.id IN (:ids)')->setParameter('ids', $ids);
        }
        if (!is_null($oldStatus)) {
            $query->andWhere('r.status = '.$oldStatus);
        }
        $query->set('r.status', $newStatus);
        return $query->getQuery()
            ->execute();
    }

    public function deleteRequest($userId, $id)
    {
        return $this->getEntityManager()->createQueryBuilder()
            ->delete('CMBundle:Request', 'r')
            ->where('r.fromUser = :user_id')->setParameter('user_id', $userId)
            ->andWhere('r.id = :id')->setParameter('id', $id)
            ->getQuery()
            ->execute();
    }

    public function getNotifications($userId, array $options = array())
    {
        $options = self::getOptions($options);
        
        $query = $this->getEntityManager()->createQueryBuilder()
            ->select('n')
            ->from('CMBundle:Notification', 'n')
            ->leftJoin('n.user', 'u')
            ->where('u.id = :id')->setParameter('id', $userId)
            ->orderBy('n.createdAt', 'desc');

        return $options['paginate'] ? $query->getQuery() : $query->setMaxResults($options['limit'])->getQuery()->getResult();
    }

    public function getNumberNewNotifications($userId)
    {
        return $this->getEntityManager()->createQueryBuilder()
            ->select('count(n.id)')
            ->from('CMBundle:Notification', 'n')
            ->leftJoin('n.user', 'u')
            ->where('u.id = :id')->setParameter('id', $userId)
            ->andWhere('n.status = :new')->setParameter('new', Notification::STATUS_NEW)
            ->getQuery()->getSingleScalarResult();
    }

    public function updateNotificationsStatus($userId, $ids, $oldStatus = null, $newStatus = Notification::STATUS_NOTIFIED)
    {
        $query = $this->getEntityManager()->createQueryBuilder()
            ->update('CMBundle:Notification', 'n')
            ->where('n.user = :id')->setParameter('id', $userId)
            ->andWhere('n.id IN (:ids)')->setParameter('ids', $ids);
        if (!is_null($oldStatus)) {
            $query->andWhere('n.status = '.$oldStatus);
        }
        $query->set('n.status', $newStatus);
        return $query->getQuery()
            ->execute();
    }
}
